import React from "react";
import { FaStar } from "react-icons/fa";
import { FaStarHalfAlt } from "react-icons/fa";
import { FiStar } from "react-icons/fi";
export default function Rating(props) {
  const { rating, numReviews } = props;
  return (
    <div className="rating">
      <span>
        <i
          className={
            rating >= 1
              ? "fa-regular fa-star"
              : rating >= 0.5
              ? "fa-regular fa-star"
              : "fa-regular fa-star"
          }
        ></i>
      </span>
      <span>
        <i
          className={
            rating >= 2 ? (
              <FaStar />
            ) : rating >= 1.5 ? (
              <FaStarHalfAlt />
            ) : (
              <FiStar />
            )
          }
        ></i>
      </span>
      <span>
        <i
          className={
            rating >= 3 ? (
              <FaStar />
            ) : rating >= 2.5 ? (
              <FaStarHalfAlt />
            ) : (
              <FiStar />
            )
          }
        ></i>
      </span>
      <span>
        <i
          className={
            rating >= 4 ? (
              <FaStar />
            ) : rating >= 3.5 ? (
              <FaStarHalfAlt />
            ) : (
              <FiStar />
            )
          }
        ></i>
      </span>
      <span>
        <i
          className={
            rating >= 5 ? (
              <FaStar />
            ) : rating >= 4.5 ? (
              <FaStarHalfAlt />
            ) : (
              <FiStar />
            )
          }
        ></i>
      </span>
      <span>{numReviews} reviews</span>
    </div>
  );
}
