import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { fastar,fastarhalfo,fastaro} from "@fortawesome/free-solid-svg-icons";
export default function Rating(props) {
  return !props.value ? (
    <div></div>
  ) : (
    <div className="rating">
      <span>
        <i
          className={
            props.value >= 1 ? (
              <FontAwesomeIcon icon={fastar} />
            ) : props.value >= 0.5 ? (
              "fa fa-star-half-o"
            ) : (
              "fa fa-star-o"
            )
          }
        ></i>
      </span>
      <span>
        <i
          className={
            props.value >= 2
              ? "fa fa-star"
              : props.value >= 1.5
              ? "fa fa-star-half-o"
              : "fa fa-star-o"
          }
        ></i>
      </span>
      <span>
        <i
          className={
            props.value >= 3
              ? "fa fa-star"
              : props.value >= 2.5
              ? "fa fa-star-half-o"
              : "fa fa-star-o"
          }
        ></i>
      </span>
      <span>
        <i
          className={
            props.value >= 4
              ? "fa fa-star"
              : props.value >= 3.5
              ? "fa fa-star-half-o"
              : "fa fa-star-o"
          }
        ></i>
      </span>
      <span>
        <i
          className={
            props.value >= 5
              ? "fa fa-star"
              : props.value >= 4.5
              ? "fa fa-star-half-o"
              : "fa fa-star-o"
          }
        ></i>
      </span>
      <span>{props.text ? props.text : ""}</span>
    </div>
  );
}
